import React, {useState, useEffect} from 'react';
import defaultTheme from './Button.module.scss';
import blueTheme from './Button2.module.scss';
import useTheme from '../../../hook/useTheme';

export const Button = (props) => {
    const [styles, setStyles] = useState(defaultTheme);
    const isDefault = useTheme(defaultTheme, blueTheme);

    useEffect(_=> {
      setStyles(isDefault);
    }, [isDefault])

    return(
        <button 
            onClick={props.onClick}
            className={`${!!styles[props.type] ? styles[props.type] : styles.btn} ${props.disabled ? styles['disabled'] : ''} ${props.size ? styles[props.size]:''}`}
            disabled={props.disabled}
            type={props.htmlType}
            style={props.style ? props.style: {}}
            >
            {!!props.icon ?
            <i className={`${styles['button__icon']} icon-${props.icon}`}></i> : null}
            {!!props.isLoading ?
            <i className={`${styles['button__icon']} ${styles['button__icon_spinner']} icon-spinner`}></i> : null}
            {props.value}
        </button> 
    )
}
