import React, {useState} from 'react';
import {Tabs} from '../../ui/components/Tabs';
import {Modal} from '../../ui/components/Modal';
import {Code} from '../components/Code';
import {Button} from '../../ui/components/Button';


export const ModalPage = () => {
    const [visible, setVisible] = useState(false)

    return(
        <div>
            <h1 className="content__hdr">Modal</h1>


            <div className="box">
                <div className="box__ite">                                     
                    <Tabs>
                        <div title="View">
                        <Button value="Show popup" onClick={_ => setVisible(true)} />

                            <Modal 
                                visible={visible}
                                close={_ => setVisible(false)}
                                title="Modal Title"
                                footer={[
                                    <Button type="link" value="Cancel" onClick={_=> setVisible(false)}/>,
                                    <Button type="primary" value="Send" />
                                ]}>
                                <p>Description modal popup</p>
                            </Modal>
                        </div>
                        <div title="Code">
                            <Code>
                                {`
const [visible, setVisible] = useState(false);

<Button value="Show popup" onClick={_=> setVisible(true)} />

<Modal 
    visible={visible}
    close={_ => setVisible(false)}
    title="Modal Title"
    footer={[
        <Button type="link" value="Cancel" onClick={_=> setVisible(false)}/>,
        <Button type="primary" value="Send" />
    ]}>
    <p>Description modal popup</p>
</Modal>`}
                            </Code>
                        </div>
                    </Tabs>
                </div>
            </div>
        </div>
    )
}
