import React, {useState} from 'react';
import icons from './icons.json';
import {Code} from '../components/Code';
import {Button} from '../../ui/components/Button';
import {Input} from '../../ui/components/Input';

export const IconsPage = () => {
    const [items, setItems] = useState(icons.items)

    const selection = (e) => {
        e.target.select();
        document.execCommand('copy');
    } 

    const changeInput = (e) => {
        const value = e.target.value;
        setItems(
            icons.items.filter(item => item.indexOf(value) !== -1)
        )
    }

    return(
        <>
            <h1 className="content__hdr">Icons</h1>
            <Input 
                type="text"
                label={'Search'}
                onChange={changeInput}
            />
            <div className="icons">
                {items.map((item, index) => (
                    <div className="icons__item" key={index}>
                        <h5 className="icons__title">{item}</h5>
                        <i className={`icon icon-${item}`}></i>
                        <textarea className="icons__field" readOnly onClick={selection} defaultValue={item}></textarea>
                    </div>
                ))}
            </div>
            <h3 className="content__hdr">Examples</h3>
            <i className="icon-search"></i>
            <Code>
                {'<i className="icon-search"></i>'}
            </Code>
            <div style={{width: 250}}>
                <Button icon="search" size="small" />
            </div>
            <Code>
                {'<Button icon="search" size="small" />'}
            </Code>
        </>
    )
}
